
interface Note {
    id: number;
    content: string;
    created_at: string;
    updated_at: string;
}

interface Collection {
    id: number;
    name: string;
}

interface CollectionView {
    id: number;
    name: string;
    editModeId: number;
    viewCategoryId: number;
    viewModeId: number;
}

interface CollectionSelection {
    id: number;
    name: string;
    note_count: number;
    last_open: string;
    favorite: boolean;
}

interface EditMode {
    id: number;
    name: string;
    class: string;
    ico: string;
}

interface PositionedNote {
    id: number;
    content: string;
    created_at: string;
    updated_at: string;
    position: number;
}

interface Positional {
    id: number;
    name: string;
    created_at: string;
    last_open: string;
    isSortable: false;
}

interface Sortable {
    id: number;
    name: string;
    sort: SortType;
    ico: string;
    isSortable: true;
}

type ViewMode = Sortable | Positional;

interface ViewModeCategory {
    id: number;
    name: string;
    ico: string;
    options: ViewMode[];
}
